+  color: lightgrey;
+}
+
+.navbar .nav-link {
+  color: #fff;
+}
+
+.navbar .nav-link:hover {
+  color: lightgrey;
+}
+
+nav .navbar-toggler-icon {
+  color: #fff;
+}
+
+body {
+  background-color: #e9ecef;
+  padding: 25px 0 0 0;
+}
+
+/* .bestiary-card {
+  background-color: #343a40;
+} */
+
+.beast-thumbnail {
+  width: auto;
+  max-height: 150px;
+}
+
+.bestiary-figure {
+  border: 2px transparent solid;
+  background-color: #fff;
+}
+
+.bestiary-figure:hover {
+  border: 2px black solid;
+}
+
+/* .card-img-top {
+  height: auto;
+  max-width: 150px;
+} */
+
+/* CSS for footer */
+footer {
+  background-color: #343a40;
+  color: #fff;
+  padding: 10px;
+  margin: 30px 0 0 0;
+  display: flex;
+  justify-content: center;
+  align-items: center;
+  border-left: 5px solid #343a40;
+}
+
+.goBackBtn {
+  position: absolute;
+  top: 60px;
+  left: 13px;
+
+  padding: .25rem .4rem;
+  font-size: .875rem;
+  line-height: .5;
+  border-radius: .2rem;
+}
+
+.jumbotron {
+  text-align: center;
+  margin-bottom: 100px;
+  min-height: 500px;
+
+  h1, p {
+    text-align: left;
+  }
+}
+
+.card {
+  padding: 20px;
+  border: 2px lightgray solid;
+  border-radius: 5%;
+  max-width: 85%;
+  margin: 30px auto;
+}
+
+#monsterPage {
+  padding: 30px 0px 150px 0px;
+  min-height: 100vh;
+  justify-content: center;
+  align-items: center;
+}
+
+.monsterName {
+  font-family: 'spindles';
+}
+
+a.list-group-flush.border-0 {
+  padding-left: 20px;
+}
+
+.monsterImageContainer {
+  display: flex;
+  justify-content: center;
+  margin: 0 0 10px 0;
+}
+
+.monsterImageContainer img {
+  border: 2px black solid;
+  border-radius: 5%;
+  background-color: #D0C6B1;
+  max-width: 300px;
+  transition: transform 0.3s ease;
+}
+
+#collapseStats {
+  border: 2px lightgray solid;
+  border-radius: 5%;
+  margin: 30px auto;
+  padding: 5px 15px;
+  background-color: white;
+  max-width: 500px;
+}
+
+#collapseStats p {
+  text-align: left;
+}
+
+#collapseStats2 {
+  border: 2px lightgray solid;
+  border-radius: 5%;
+  margin: 30px auto;
+  padding: 5px 15px;
+  background-color: white;
+  max-width: 500px;
+}
+
+#collapseStats2 p {
+  text-align: left;
+}
+
+#collapseLore {
+  border: 2px lightgray solid;
+  border-radius: 5%;
+  margin: 30px auto;
+  padding: 5px 15px;
+  background-color: white;
+  max-width: 500px;
+}
+
+#collapseLore p {
+  text-align: left;
+}
+
+li {
+  text-align: left;
+}
+
+.tableContainer {
+  display: flex;
+  justify-content: center;
+  border-radius: 10px;
+}
+
+table {
+  border-collapse: collapse;
+  width: 300px;
+}
+
+.tg {
+  border-collapse: collapse;
+  border-spacing: 0;
+}
+
+.tg td {
+  border-color: black;
+  border-style: solid;
+  border-width: 1px;
+  font-family: Arial, sans-serif;
+  font-size: 14px;
+  overflow: hidden;
+  padding: 10px 5px;
+  word-break: normal;
+}
+
+.tg th {
+  border-color: black;
+  border-style: solid;
+  border-width: 1px;
+  font-family: Arial, sans-serif;
+  font-size: 14px;
+  font-weight: normal;
+  overflow: hidden;
+  padding: 10px 5px;
+  word-break: normal;
+}
+
+.tg .tg-uqo3 {
+  background-color: #efefef;
+  text-align: center;
+  vertical-align: top
+}
+
+.tg .tg-yj5y {
+  background-color: #efefef;
+  border-color: inherit;
+  text-align: center;
+  vertical-align: top
+}
+
+.tg .tg-34fe {
+  background-color: #c0c0c0;
+  border-color: inherit;
+  text-align: center;
+  vertical-align: top
+}
+
+.bold-text {
+  font-weight: bold;
+}
+
+.horizontal-line {
+  border-top: 2px solid black;
+  width: 50%;
+  margin: 12px auto;
+  display: flex;
+  justify-content: center;
+  align-items: center;
+
+  @media screen and (max-width: 768px) {
+    width: 80%;
+    margin: 10px auto;
+  }
+
+  @media screen and (max-width: 480px) {
+    width: 80%;
+    margin: 10px auto;
+  }
+}
+
+/* Monster Page Specific */
+
+.monsterBtn {
+  margin: 5px;
+}
+
+.critical-failure {
+  color: red;
+  font-weight: bold;
+}
+
+.critical-success {
+  color: green;
+  font-weight: bold;
+}
+
+.attributeForm {
+  max-width: 50px;
+}
+
+#characterPage {
+  padding: 50px 15px 150px 15px;
+  min-height: 100vh;
+  justify-content: center;
+  align-items: center;
+}
+
+/* Character Sheet Specific */
+
+.attributes {
+  border: 2px solid black;
+  padding: 5px;
+  max-width: auto;
+}
