+  border-left: 5px solid #343a40;
+}
+
+.goBackBtn {
+  position: absolute;
+  top: 60px;
+  left: 13px;
+
+  padding: .25rem .4rem;
+  font-size: .875rem;
+  line-height: .5;
+  border-radius: .2rem;
+}
+
+.jumbotron {
+  text-align: center;
+  margin-bottom: 100px;
+  min-height: 500px;
+
+  h1, p {
+    text-align: left;
+  }
+}
+
+.card {
+  padding: 20px;
+  border: 2px lightgray solid;
+  border-radius: 5%;
+  max-width: 85%;
+  margin: 30px auto;
+}
+
+#monsterPage {
+  padding: 0px 0px 150px 0px;
+  min-height: 100vh;
+  justify-content: center;
+  align-items: center;
+}
+
+.monsterName {
+  font-family: 'spindles';
+}
+
+a.list-group-flush.border-0 {
+  padding-left: 20px;
+}
+
+.monsterImageContainer {
+  display: flex;
